%spark
// scala case class
case class Flight(id: String,fldate: String,month:Integer, dofW: Integer,carrier: String, src: String,dst: String, crsdephour: Integer, crsdeptime: Integer,depdelay: Double, crsarrtime: Integer, arrdelay: Double, crselapsedtime: Double, dist:Double,orig_dest: String, label: Double, prediction: Double)

// create a GraphFrame by supplying a vertex airports DataFrame and an edge flights DataFrame
val graph = GraphFrame(airports, df)

graph.vertices.show

// What is the count of flights for all possible flight routes, sorted? 
// note we will use the flightroutecount DataFrame later
val flightroutecount = graph.edges.groupBy("src", "dst").count().orderBy(desc("count"))
// show 10 most frequent flight routes in the data set
flightroutecount.show(10)

val ranks = graph.pageRank.resetProbability(0.15).maxIter(10).run()
// PageRank results are in a new column appended to the vertices DataFrame

ranks.vertices.orderBy($"pagerank".desc).show()

//search flight routes that have a path to (a,b) and a path to (b,c) but not a path to (a,c)
val temp = GraphFrame(graph.vertices, flightroutecount)
val res = temp.find("(a)-[]->(b); (b)-[]->(c); !(c)-[]->(a)").filter("c.id !=a.id")
//res.select($"a", $"c").distinct.show
z.show(res.select($"a", $"c").distinct)

//Breadth First Search for Direcct Flights between LAX and LGA
//Here we set the maxPathLength to 1 
//The results show no direct flights 
val paths = graph.bfs.fromExpr("id = 'LAX'").toExpr("id = 'LGA'").maxPathLength(1).run()
paths.show()

// Graph queries can be combined with DataFrame operations like filter.
// Motif query to search for the pattern of a flying to c, connecting through b 
// DataFrame filter on the the results for A=LAX and C=LGA .
// results show some flights connecting through IAH for flights from LAX to LGA .
z.show(graph.find("(a)-[ab]->(b); (b)-[bc]->(c)").filter("a.id = 'LAX'").filter("c.id = 'LGA'").limit(4))

//Breadth First Search for Flights between LAX and LGA with one connection
//for one connection set the maxPathLength to 2
// The results show connections through Houston IAH 
z.show(graph.bfs.fromExpr("id = 'LAX'").toExpr("id = 'LGA'").maxPathLength(2).run().limit(4))

//Breadth First Search for Direcct Flights between LGA and SFO
//Here we set the maxPathLength to 1 
//The results show no direct flights 
graph.bfs.fromExpr("id = 'LGA'").toExpr("id = 'SFO'").maxPathLength(1).run().show()

//Here we set the maxPathLength to 2. 
//The results show some flights connecting through IAH for flights from LAX to LGA 
graph.bfs.fromExpr("id = 'LGA'").toExpr("id = 'SFO'").edgeFilter("carrier='UA'").maxPathLength(2).run().show(5)

//for direct flights set the maxPathLength to 1. 
//The results show no direct flights
graph.bfs.fromExpr("id = 'SEA'").toExpr("id = 'LGA'").maxPathLength(1).run().show()

//for flight routes with one connection set the maxPathLength to 2. 
//The results shows connections through texas
graph.bfs.fromExpr("id = 'SEA'").toExpr("id = 'LGA'").edgeFilter("carrier='UA'").maxPathLength(2).run().show(5)

// use motif to find connecting flights
//  filter for LAX->IAH->LGA 
//  filter for delay > 40
//  filter for fldate 
val paths = graph.find("(a)-[ab]->(b); (b)-[bc]->(c)").filter("(a.id =='LAX') and (b.id =='IAH') and (c.id =='LGA') and (bc.arrdelay>40) and (ab.arrdelay>40)and(bc.crsdephour + bc.depdelay > ab.crsdephour + ab.arrdelay ) and (ab.fldate == bc.fldate)").limit(20)

z.show(paths.select($"ab", $"bc"))
